package operators;

public class OperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		//Arithmetic operator
		
	//	+,-,*,/,%
		
		int a=10;
		int b=20;
		
		
		int c=a+b;
		int d=a-b;
		int e=a*b;
		int f=a/b;
		int g=a%b; //remainder after divison exam 5/2 == 1 5%2 == 1
		System.out.println(c + " "+d +" "+ e+ "  "+f+" "+g);
		
	}
	

}
